import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getActivities, postActivities } from "../../Redux/actions";
import { SUMMER, FALL, WINTER, SPRING } from "../../Const/Const";

function validate(input) {
  let errors = {};
  let aux = input.duration
  if (!input.name) {
    errors.name = "You must complete this field";
  } else if (!input.duration) {
    errors.duration = "You must complete this field";
  }else if(aux !== "30" || aux !== "60" || aux !==  "90" || aux !== 120 || aux !== 150 || aux!= 180 ){
    errors.duration = "You ingresaste cualquiera";
  }
   else if (!input.difficulty) {
    errors.difficulty = "You must select the complexity";
  } else if (!input.season) {
    errors.season = "You must select a station";
  } else if (input.countryId === []) {
    errors.countryId = "You must select a country";
  }
  return errors;
}

const ActivityCreate = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const countries = useSelector((state) => state.allCountries);
  const [errors, setErrors] = useState({});

  const [input, setInput] = useState({
    name: "",
    duration: "",
    difficulty: "",
    season: "",
    countryId: [],
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    setErrors(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleDelete(i) {
    setInput({
      ...input,
      countryId: input.countryId.filter((el) => el !== i),
    });
  }

  function handleSelect(e) {
    setInput({
      ...input,
      countryId: [...input.countryId, e.target.value],
    });
  }

  useEffect(() => {
    dispatch(getActivities());
  }, [dispatch]);

  function handleSubmit(e) {
    e.preventDefault();
    if (
      input.name === "" ||
      input.duration === "" ||
      input.difficulty === "" ||
      input.season === "" ||
      input.countryId.length === 0
    )
      return alert("You must fill in the fields");

    dispatch(postActivities(input));
    alert("Actity Created");
    setInput({
      name: "",
      duration: "",
      difficulty: "",
      season: "",
      countryId: [],
    });
    history.push("/home");
  }

  return (
    <div>
      <div className="activityCardContainer">
        <div className="activityCard">
          <div className="activityTitle"></div>

          <form className="formActivity" onSubmit={handleSubmit}>
            <span className="titleCreateActivity"> Create an Activity </span>
            <div className="inputActivities">
              <label className="labelActivity"></label>
              <input
                className="i"
                type="text"
                placeholder="Place the Activity...  "
                value={input.name}
                name="name"
                onChange={handleChange}
              />
              {errors.name && <p className="e">{errors.name}</p>}
            </div>
            {/* '30', '60', '90', '120', '150', '180', '210', '240' */}
            <div className="inputActivities">
              <label>
                The duration of each activity is in shifts of 30 minutes
                interspersed, with a maximum of 240 minutes.
              </label>
              <input
                className="i"
                type="text"
                value={input.duration}
                name="duration"
                placeholder="Enter the Duration..."
                onChange={handleChange}
              />
              {errors.duration && <p className="e">{errors.duration}</p>}
            </div>

            <div className="inputActivities">
              <label> Difficulty </label>
              <select
                name="difficulty"
                value={input.difficulty}
                onChange={(e) => handleChange(e)}
              >
                <option className="op" value={"1"}>
                  1
                </option>
                <option className="op" value={"2"}>
                  2
                </option>
                <option className="op" value={"3"}>
                  3
                </option>
                <option className="op" value={"4"}>
                  4
                </option>
                <option className="op" value={"5"}>
                  5
                </option>
              </select>
              {/* <input
                className="i"
                type="range"
                name="difficulty"
                min="1"
                max="5"
                value={input.difficulty}
                onChange={(e) => handleChange(e)}
              /> */}
              {errors.difficulty && <p className="e"> {errors.difficulty}</p>}
            </div>
            <div className="seasonInput">
              <select
                className="i"
                name="season"
                value={input.season}
                onChange={(e) => handleChange(e)}
              >
                <option className="op"> Season </option>
                <option className="op" value={WINTER}>
                  Winter
                </option>
                <option className="op" value={SUMMER}>
                  Summer
                </option>
                <option className="op" value={FALL}>
                  Fall
                </option>
                <option className="op" value={SPRING}>
                  Spring
                </option>
              </select>
              {errors.season && <p className="e">{errors.season}</p>}
            </div>
            {errors.countryId && <p className="e">{errors.countryId}</p>}

            <div>
              <select className="i" onChange={(e) => handleSelect(e)}>
                <option className="op"> Countries </option>
                {countries.map((v) => (
                  <option className="op" value={v.id}>
                    {v.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="textArea">
              {input.countryId.map((country) => (
                <div className="countrieAndButton">
                  <input
                    className="btnDelete"
                    type="button"
                    value="X"
                    onClick={() => handleDelete(country)}
                  />
                  <p className="pOfCountry">{country}</p>
                </div>
              ))}
            </div>
            <div>
              <button className="btnActivity" type="submit">
                Create Activity
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ActivityCreate;
